    int totalSwaps = 0;
    
    void performMergeSort () {
        mergeSort(arr, 0, arr.length - 1);
        System.out.println("Total Swaps : " + totalSwaps);
    }
    
    
    void mergeSort (int[] arr, int p, int r) {
        System.out.println("MERGE SORT p = " + p +  " r = " + r);
        if (p < r) {
            int q = (p + r) / 2;
            mergeSort(arr, p, q);
            mergeSort(arr, q + 1, r);
            System.out.println("MERGE p = " + p + " q = " + q + " r = " + r);
            merge(arr,p, q, r);
        }
        
        
    }
    
    
    void merge (int[] arr, int p, int q, int r) {
        
        int lengthOfFirstArray = q - p + 1;
        int lengthOfSecondArray = r - q;
        int[] left = new int[lengthOfFirstArray + 1];
        int[] right = new int[lengthOfSecondArray + 1];
        for (int i = 0; i < lengthOfFirstArray; i++) {
            left[i] = arr[p + i];
        }
        left[lengthOfFirstArray] = Integer.MAX_VALUE;
        for (int i = 0; i < lengthOfSecondArray; i++) {
            right[i] = arr[q + i + 1];
        }
        right[lengthOfSecondArray] = Integer.MAX_VALUE;
        System.out.println("Left = " + lengthOfFirstArray);
        printArray(left);
        System.out.println("Right = " + lengthOfSecondArray);
        printArray(right);
        int i = 0, j = 0;
        for (int k = p; k <= r; k++) {
            if (left[i] <= right[j]) {
                arr[k] = left[i];
                i++;
                
            }
            else{
                arr[k] = right[j];
                j++;
                if(i != lengthOfFirstArray){
                    totalSwaps ++;
                    System.out.println("Swap : " + totalSwaps);
                }
            }
        }
        System.out.println("ORIGNAL ARRAY");
        printArray(arr);
    }
    
    void printArray (int[] array) {
        System.out.println();
        for (int i = 0; i < array.length; i++) {
            System.out.print(array[i] + " ");
        }
        System.out.println();
    }
    
    
